import './header.css';
import HeadItem from './HeadItem'
import { AiOutlineSearch } from 'react-icons/ai';
import { IoPersonCircleOutline , IoPersonCircleSharp } from "react-icons/io5";

function Header() {

  function SearchHandler(){
    const search=document.activeElement
    if (search.classList.value==="search"){
      search.parentElement.childNodes[0].classList.add("passive");
    }
    if(search.value===""){
      search.parentElement.childNodes[0].classList.remove("passive")
    }
  }

  function NavHandler(){
    const items=document.querySelectorAll(".HeadItem-wrapper")
    items.forEach(item =>{
        item.addEventListener("mouseover",() => {
            const icons=item.childNodes[0]
            icons.childNodes[0].classList.remove('active');
            icons.childNodes[1].classList.add('active');
        })
    })
    items.forEach(item =>{
        item.addEventListener("mouseout",() => {
            const icons=item.childNodes[0];
            icons.childNodes[0].classList.add('active');
            icons.childNodes[1].classList.remove('active');
        })
    })
    }

  return (
    <div className="App-header" >
      <div className="left-wrapper">
        <span className="icon-wrapper"></span>
        <label>Microsoft Teams</label>
      </div>
      <div className="search-wrapper">
        <i><AiOutlineSearch className="icons" color="gray" size="1.1em" /><label>Search</label></i>
        <input className="search" onChange={SearchHandler}/>

      </div>
      <div className="right-wrapper" >
        <HeadItem iconA={<IoPersonCircleOutline/>} iconB={<IoPersonCircleSharp/>}/>
        <div className="spacer"></div>
      </div>
    </div>
  );
}

export default Header;
