import './navbar.css';
import {IconContext} from 'react-icons'
import NavItem from './NavItem';
import { BsBell , BsBellFill, BsChatDots , BsChatDotsFill , BsCalendar , BsCalendarFill , BsThreeDots} from "react-icons/bs";
import { HiOutlineUserGroup , HiUserGroup , HiOutlineShoppingBag , HiShoppingBag } from "react-icons/hi";
import { IoCallOutline , IoCallSharp , IoHelpCircleSharp , IoHelpCircleOutline } from "react-icons/io5";
import { AiOutlineFile , AiFillFile , AiOutlineAppstore , AiTwotoneAppstore} from "react-icons/ai";

function Navbar(){
    function NavHandler(){
        const items=document.querySelectorAll(".NavItem-wrapper")
        items.forEach(item =>{
            item.addEventListener("mouseover",() => {
                const icons=item.childNodes[0]
                icons.childNodes[0].classList.remove('active');
                icons.childNodes[1].classList.add('active');
            })
        })
        items.forEach(item =>{
            item.addEventListener("mouseout",() => {
                const icons=item.childNodes[0];
                icons.childNodes[0].classList.add('active');
                icons.childNodes[1].classList.remove('active');
            })
        })
        }
    return(
        <IconContext.Provider value={  {color:"white" , size:"1.3em" } }>
            <div className='Navbar-wrapper' onMouseEnter={NavHandler}>
                <div className='Navbar-topitem'>
                    <NavItem iconA={<BsBell className="icon"/>} iconB={<BsBellFill/>} title="Activity"/>
                    <NavItem iconA={<BsChatDots className="icon"/>} iconB={<BsChatDotsFill/>} title="Chats"/>
                    <NavItem iconA={<HiOutlineUserGroup className="icon"/>} iconB={<HiUserGroup/>} title="Teams"/>
                    <NavItem iconA={<HiOutlineShoppingBag className="icon"/>} iconB={<HiShoppingBag/>} title="Assignments"/>
                    <NavItem iconA={<BsCalendar className="icon"/>} iconB={<BsCalendarFill/>} title="Calendar"/>
                    <NavItem iconA={<IoCallOutline className="icon"/>} iconB={<IoCallSharp/>} title="Calls"/>
                    <NavItem iconA={<AiOutlineFile className="icon"/>} iconB={<AiFillFile/>} title="Files"/>
                    <div className='settings-wrapper'>
                        <i></i><BsThreeDots/>
                    </div>

            </div>
            <div className='Navbar-bottomitem'>
                    <NavItem iconA={<AiOutlineAppstore className="icon"/>} iconB={<AiTwotoneAppstore/>} title="Apps"/>
                    <NavItem iconA={<IoHelpCircleOutline className="icon"/>} iconB={<IoHelpCircleSharp/>} title="Help"/>
            </div>
        </div>
        </IconContext.Provider>

    );
}

export default Navbar;
