import './navitem.css'

function Navtop(props){
    const icon=document.querySelector('.icon')
    console.log(icon)
    icon.addEventListener("onmouseover",(icon) =>{
        icon.classList.add('active')
    });
    // icon.addEventListener("onmouseover",(icon) => {
    //     icon.classList.add('active')
    // })
    return(
        <div className='NavItem-wrapper'>
            <i>{props.icon}</i>
            <label>{props.title}</label>
        </div>
    );
}

export default Navtop;
